import React, { useState } from 'react';
import './App.css';

function App() {
  const [textInput, setTextInput] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [downloadUrl, setDownloadUrl] = useState("");
  const [progress, setProgress] = useState(0);
  const [numSlides, setNumSlides] = useState(5); // Added state for number of slides

  const simulateProgress = () => {
    setProgress(0);
    let currentProgress = 0;
    const interval = setInterval(() => {
      currentProgress += Math.random() * 10 + 5; // More consistent progress
      if (currentProgress >= 95 && !downloadUrl) { // Hold at 95 if not done
        // clearInterval(interval); // Don't clear yet, wait for actual completion
        setProgress(95);
      } else if (currentProgress < 95) {
        setProgress(currentProgress);
      } else if (downloadUrl) { // If done, jump to 100
        clearInterval(interval);
        setProgress(100);
      }
    }, 300);
    return interval;
  };

  const handleGenerate = async () => {
    console.log("handleGenerate called");
    setLoading(true);
    setError("");
    setDownloadUrl("");
    const progressInterval = simulateProgress();

    try {
      const backendUrl = process.env.REACT_APP_BACKEND_URL || "http://localhost:8000";
      const requestBody = {
        text_input: textInput,
        num_slides: parseInt(numSlides) || 5,
      };
      console.log("Sending request to backend:", `${backendUrl}/generate-ppt/`, "with body:", JSON.stringify(requestBody));

      const response = await fetch(`${backendUrl}/generate-ppt/`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(requestBody),
      });

      console.log("Backend response status:", response.status, response.statusText);

      const responseData = await response.json();
      console.log("Backend response data:", responseData);

      if (!response.ok) {
        const errorMessage = responseData.detail?.[0]?.msg || responseData.detail || responseData.message || `Request failed with status ${response.status}`;
        console.error("API Error from backend:", errorMessage);
        throw new Error(errorMessage);
      }

      if (responseData.download_url) {
        setDownloadUrl(responseData.download_url);
        console.log("Download URL set:", responseData.download_url);
        setProgress(100); // Progress to 100 on success
        clearInterval(progressInterval); // Clear interval on success
      } else {
        console.error("Backend response OK, but no download_url found in data:", responseData);
        throw new Error("Failed to get download link from server.");
      }

    } catch (err) {
      console.error("Error in handleGenerate (fetch or processing):", err);
      setError(err.message || "Failed to generate presentation. Please try again.");
      setProgress(0); // Reset progress on error
      clearInterval(progressInterval); // Clear interval on error
    } finally {
      // setLoading(false); // setLoading will be false when downloadUrl is set or error occurs
      // No need for setTimeout for progress reset if handled on error/success
      // Ensure loading is set to false IF an error occurred and downloadUrl was not set
      if (!downloadUrl && !error) { // Edge case if something unexpected happens
          // This might be too aggressive, let's rely on downloadUrl or error to stop loading
      }
       // If still loading after all this and no downloadUrl, implies it's an error state
       // However, the try/catch should handle setting error and thus loading state via useEffect or direct set.
       // For now, we'll ensure loading is false if we exit this block.
       // If an error was caught, loading should be false. If success, it should be false too.
       // The `loading` state should primarily control the button disable and spinner visibility.
       // It should be set to `false` when the operation (success or failure) is fully complete.
       // The code above already sets progress to 100 or 0 and clears interval.
       // Setting loading to false will happen after downloadUrl is set or error is set.
       // Let's ensure it happens after the try-catch-finally completes for clarity
       setLoading(false); // Moved here to ensure it's always set after operation
    }
  };

  const triggerDownload = () => {
    if (downloadUrl) {
      console.log("Attempting to trigger download for URL:", downloadUrl);
      window.location.href = downloadUrl;
      // Optionally, reset downloadUrl after initiating download if you want the button to disappear
      // setDownloadUrl(""); 
    } else {
      console.log("No download URL available to trigger download.");
    }
  };

  const exampleTopics = [
    "Effective Time Management", "Basics of Quantum Computing", "The Future of Renewable Energy",
    "Content Marketing for Startups", "Mindfulness in the Workplace"
  ];

  return (
    <div className="app-container">
      <div className="app-wrapper">
        <div className="main-card">
          <div className="bg-element bg-element-1"></div>
          <div className="bg-element bg-element-2"></div>
          <div className="header">
            <div className="icon-container"><span className="icon">üé®</span></div>
            <h1 className="title">AI PowerPoint Generator</h1>
            <p className="subtitle">Transform text into presentations instantly</p>
          </div>

          <div className="form-group">
            <label className="form-label">üìù Enter text or a detailed topic for your presentation:</label>
            <div className="input-container">
              <textarea
                className="form-input"
                placeholder="e.g., A detailed overview of the impact of AI on modern healthcare, including ethical considerations and future prospects..."
                value={textInput}
                onChange={(e) => setTextInput(e.target.value)}
                rows={5}
              />
              {textInput && (<div className="input-check">‚úì</div>)}
            </div>
            {!textInput && (
              <div className="suggestions">
                <p className="suggestions-label">üí° Try these examples (elaborate in the text area):</p>
                <div className="suggestions-list">
                  {exampleTopics.slice(0, 3).map((example, idx) => (
                    <button key={idx} onClick={() => setTextInput(example)} className="suggestion-btn">{example}</button>
                  ))}
                </div>
              </div>
            )}
          </div>

          <div className="form-group">
            <label htmlFor="numSlides" className="form-label">‚öôÔ∏è Number of Content Slides (approx.):</label>
            <input
              type="number"
              id="numSlides"
              className="form-input form-input-small"
              value={numSlides}
              onChange={(e) => setNumSlides(parseInt(e.target.value) || 5)}
              min="1"
              max="15"
            />
          </div>

          <button
            onClick={handleGenerate}
            disabled={loading || !textInput.trim()}
            className={`generate-btn ${loading || !textInput.trim() ? "generate-btn-disabled" : ""}`}
          >
            {loading ? (
              <div className="btn-content"><div className="spinner"></div>Generating...</div>
            ) : (
              <div className="btn-content"><span className="btn-icon">‚ú®</span>Generate Presentation</div>
            )}
          </button>

          {loading && (
            <div className="progress-container">
              <div className="progress-text">
                <span>Creating your masterpiece...</span>
                <span>{Math.round(progress)}%</span>
              </div>
              <div className="progress-bar"><div className="progress-fill" style={{ width: `${progress}%` }}></div></div>
            </div>
          )}

          {downloadUrl && !loading && (
            <button onClick={triggerDownload} className="download-btn">
              <div className="btn-content"><span className="btn-icon">üì•</span>Download Your PPT</div>
            </button>
          )}

          {error && (
            <div className="error-container">
              <div className="error-content">
                <span className="error-icon">‚ö†Ô∏è</span>
                <div className="error-text">
                  <p className="error-title">An Error Occurred</p>
                  <p className="error-message">{error}</p>
                </div>
              </div>
            </div>
          )}
        </div>
        <div className="footer"><p className="footer-text">Powered by Generative AI</p></div>
      </div>
    </div>
  );
}

export default App;
