import React, { useState, useEffect } from 'react'; // Added useEffect for potential future use
import './App.css';

function App() {
  // Changed 'topic' to 'textInput' to match backend expectation more closely internally
  // The UI can still refer to it as "topic" for the user
  const [textInput, setTextInput] = useState("");
  // const [tone, setTone] = useState("formal"); // Backend doesn't use this currently, so commenting out for now
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  // Instead of pptBlob, we'll store the download URL from the backend
  const [downloadUrl, setDownloadUrl] = useState("");
  const [progress, setProgress] = useState(0);

  // Simulate progress for better UX
  const simulateProgress = () => {
    setProgress(0);
    let currentProgress = 0;
    const interval = setInterval(() => {
      currentProgress += Math.random() * 15;
      if (currentProgress >= 90) {
        clearInterval(interval);
        setProgress(90);
      } else {
        setProgress(currentProgress);
      }
    }, 200);
    return interval;
  };

  const handleGenerate = async () => {
    setLoading(true);
    setError("");
    setDownloadUrl(""); // Reset download URL
    const progressInterval = simulateProgress();

    try {
      const backendUrl = process.env.REACT_APP_BACKEND_URL || "http://localhost:8000";

      // Construct the request body as expected by the backend
      const requestBody = {
        text_input: textInput,
        // num_slides: 5 // You can add this if you want to control it from frontend
      };

      const response = await fetch(`${backendUrl}/generate-ppt/`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(requestBody), // Send text_input
      });

      const responseData = await response.json(); // Always expect JSON first

      if (!response.ok) {
        // FastAPI validation errors are often in responseData.detail
        const errorMessage = responseData.detail?.[0]?.msg || responseData.detail || responseData.message || "An unknown error occurred";
        throw new Error(errorMessage);
      }

      // Backend now returns a JSON with download_url
      if (responseData.download_url) {
        setDownloadUrl(responseData.download_url);
        setProgress(100);
      } else {
        throw new Error("Failed to get download link from server.");
      }

    } catch (err) {
      console.error("Error in handleGenerate:", err);
      setError(err.message || "Failed to generate presentation. Please try again.");
      setProgress(0); // Reset progress on error
    } finally {
      clearInterval(progressInterval);
      setLoading(false);
      // No need to setTimeout for progress reset here if it's handled on error/success
    }
  };

  // This function is triggered when the user clicks the download button
  const triggerDownload = () => {
    if (downloadUrl) {
      // Simplest way to trigger download is to navigate to the URL
      // The browser will handle it if the backend sends correct Content-Disposition
      window.location.href = downloadUrl;
      // After triggering, you might want to clear the downloadUrl
      // so the button disappears, or allow re-download
      // setDownloadUrl(""); 
    }
  };


  const exampleTopics = [
    "Climate Change Solutions",
    "Introduction to AI",
    "Digital Marketing Strategy",
    "Remote Work Best Practices",
    "Sustainable Business Models"
  ];

  return (
    <div className="app-container">
      <div className="app-wrapper">
        {/* Main Card */}
        <div className="main-card">
          {/* Animated background elements */}
          <div className="bg-element bg-element-1"></div>
          <div className="bg-element bg-element-2"></div>

          {/* Header */}
          <div className="header">
            <div className="icon-container">
              <span className="icon">🎨</span>
            </div>
            <h1 className="title">AI PowerPoint Generator</h1>
            <p className="subtitle">Create stunning presentations in seconds</p>
          </div>

          {/* Topic Input (UI still says topic, but state is textInput) */}
          <div className="form-group">
            <label className="form-label">
              📝 What's your presentation about? (Provide detailed text or a topic for the AI to expand)
            </label>
            <div className="input-container">
              {/* Changed to textarea for potentially longer input */}
              <textarea
                className="form-input"
                placeholder="Enter the text for your presentation, or a detailed topic..."
                value={textInput}
                onChange={(e) => setTextInput(e.target.value)}
                rows={4} 
              />
              {textInput && (
                <div className="input-check">✓</div>
              )}
            </div>

            {/* Topic Suggestions */}
            {!textInput && (
              <div className="suggestions">
                <p className="suggestions-label">💡 Try these example topics (you can elaborate on them in the text area):</p>
                <div className="suggestions-list">
                  {exampleTopics.slice(0, 3).map((example, idx) => (
                    <button
                      key={idx}
                      onClick={() => setTextInput(example)} // Sets textInput
                      className="suggestion-btn"
                    >
                      {example}
                    </button>
                  ))}
                </div>
              </div>
            )}
          </div>

          {/* Tone Selection - Commented out as backend doesn't use it yet */}
          {/* 
          <div className="form-group">
            <label className="form-label">
              🎭 Choose your style
            </label>
            <div className="tone-buttons">
              <button
                onClick={() => setTone("formal")}
                className={`tone-btn ${tone === "formal" ? "tone-btn-active" : ""}`}
              >
                <div className="tone-icon">🎯</div>
                Professional
              </button>
              <button
                onClick={() => setTone("informal")}
                className={`tone-btn ${tone === "informal" ? "tone-btn-active" : ""}`}
              >
                <div className="tone-icon">😊</div>
                Friendly
              </button>
            </div>
          </div>
          */}

          {/* Generate Button */}
          <button
            onClick={handleGenerate}
            disabled={loading || !textInput.trim()} // Uses textInput
            className={`generate-btn ${loading || !textInput.trim() ? "generate-btn-disabled" : ""}`}
          >
            {loading ? (
              <div className="btn-content">
                <div className="spinner"></div>
                Generating Magic...
              </div>
            ) : (
              <div className="btn-content">
                <span className="btn-icon">✨</span>
                Generate Presentation
              </div>
            )}
          </button>

          {/* Progress Bar */}
          {loading && (
            <div className="progress-container">
              <div className="progress-text">
                <span>Creating your presentation...</span>
                <span>{Math.round(progress)}%</span>
              </div>
              <div className="progress-bar">
                <div 
                  className="progress-fill"
                  style={{ width: `${progress}%` }}
                ></div>
              </div>
            </div>
          )}

          {/* Download Button - Shows when downloadUrl is available */}
          {downloadUrl && !loading && (
            <button
              onClick={triggerDownload}
              className="download-btn"
            >
              <div className="btn-content">
                <span className="btn-icon">📥</span>
                Download Your PPT
              </div>
            </button>
          )}

          {/* Error Message */}
          {error && (
            <div className="error-container">
              <div className="error-content">
                <span className="error-icon">⚠️</span>
                <div className="error-text">
                  <p className="error-title">Oops! Something went wrong</p>
                  <p className="error-message">{error}</p>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Footer */}
        <div className="footer">
          <p className="footer-text">
            Powered by AI • Create professional presentations instantly
          </p>
        </div>
      </div>
    </div>
  );
}

export default App;
